import 'package:firebase_auth/firebase_auth.dart';
import 'package:firebase_database/firebase_database.dart';
import 'package:flutter/material.dart';
import 'package:fluttertoast/fluttertoast.dart';
import 'package:project_detect_disease_datn/components/default_button.dart';
import 'package:project_detect_disease_datn/constants.dart';
import 'package:project_detect_disease_datn/model/disease_plants.dart';
import 'package:project_detect_disease_datn/utils/ProgressHuD.dart';

class UploadData extends StatefulWidget {
  static String routeName = "/upload_data";
  const UploadData({Key? key}) : super(key: key);

  @override
  _UploadDataState createState() => _UploadDataState();
}

class _UploadDataState extends State<UploadData> {
  bool uploading = false;
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text("Upload data"),
      ),
      body: ProgressHUD(
          inAsyncCall: uploading,
          opacity: 0.1,
          child: Center(
            child: DefaultButton(
              color: kPrimaryColor,
              press: () async {
                setState(() {
                  uploading = true;
                });
                saveToDatabase().whenComplete(() {
                  setState(() {
                    uploading = false;
                  });
                  Fluttertoast.showToast(msg: "Upload data success :)");
                });
              },
              text: "Upload Data",
            ),
          )),
    );
  }

  Future<void> saveToDatabase() async {
    FirebaseDatabase database = FirebaseDatabase.instance;
    DatabaseReference myRef = database.reference().child("Disease_Ex").push();
    String key = myRef.key;
    DiseasePlants post = DiseasePlants(
        describe:
            "<p><strong>T&ecirc;n tiếng Anh: Blueberry</strong></p>\n<p><strong>T&ecirc;n tiếng Việt: Việt quất</strong></p>\n<p><strong>Đặc điểm:</strong></p>\n<p>T&ecirc;n gọi: Việt Quất, Ỏng Ảnh, Sim &Uacute;c</p>\n<p>Nguồn gốc: Bắc Mỹ, Ch&acirc;u &Acirc;u, &Uacute;c</p>\n<p>T&ecirc;n khoa học : Blueberry, Vaccinium myrtillus</p>\n<p>Họ Thạch nam.</p>\n<p>Việt Quất c&oacute; th&acirc;n gỗ dạng th&acirc;n bụi (mọc th&agrave;nh bụi). Mọc thẳng đứng hoặc s&aacute;t đất, chiều cao từ 30cm &ndash; 4m.</p>\n<p>L&aacute; Việt Quất h&igrave;nh elip, m&agrave;u xanh lam nhạt, đậm hơn v&agrave;o m&ugrave;a h&egrave; v&agrave; ngả t&iacute;m khi m&ugrave;a thu.</p>\n<p>Hoa h&igrave;nh chu&ocirc;ng, m&agrave;u trắng, d&agrave;i khoảng 4-5cm, hoa nở v&agrave;o m&ugrave;a xu&acirc;n.</p>\n<p>Quả Việt Quất giống quả vối của Việt Nam nhưng to hơn một ch&uacute;t, m&agrave;u t&iacute;m than, hoặc đen nhạt. Thịt quả m&agrave;u t&iacute;m phớt hồng. Quả thường c&oacute; v&agrave;o m&ugrave;a h&egrave;, thường mọc th&agrave;nh ch&ugrave;m. C&acirc;y bụi cao c&oacute; khả năng cho 5-6kg quả, c&acirc;y nhỏ s&aacute;t đất thường cho tầm 1-1,5kg.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://i.ytimg.com/vi/cNjy-9PAqWI/maxresdefault.jpg\" alt=\"K&aacute;&ordm;&iquest;t qu&aacute;&ordm;&pound; h&Atilde;&not;nh &aacute;&ordm;&pound;nh cho c&Atilde;&cent;y vi&aacute;&raquo;t qu&aacute;&ordm;&yen;t\" width=\"334\" height=\"188\" /></p>\n<p><strong>C&aacute;ch chăm s&oacute;c cho c&acirc;y Việt Quất:</strong></p>\n<p>Sau khi c&acirc;y cao được chừng 15-20cm, ta tiến h&agrave;nh trồng c&acirc;y ở khu vực định sẵn. Ch&uacute; &yacute; b&oacute;n l&oacute;t ph&acirc;n chuồng, ph&acirc;n hữu cơ cho đất trước 1 th&aacute;ng v&agrave; tưới nước duy tr&igrave; độ ẩm khoảng 1 th&aacute;ng sau khi trồng.</p>\n<p>Ch&uacute; &yacute; khi trồng trong chậu:&nbsp;Chậu phải c&oacute; k&iacute;ch thước lớn: 40x60cm trở l&ecirc;n. Giữ ẩm v&agrave; b&oacute;n ph&acirc;n, c&aacute;c chất dinh dưỡng thường xuy&ecirc;n cho c&acirc;y v&igrave; lượng đất &iacute;t, c&acirc;y dễ bị kh&ocirc; hạn.</p>\n<p>Sau khi c&acirc;y được 3 th&aacute;ng tuổi, tiến h&agrave;nh b&oacute;n ph&acirc;n chuồng, kết hợp NPK để k&iacute;ch th&iacute;ch c&acirc;y ph&aacute;t triển. Ta c&oacute; thể b&oacute;n theo liều lượng v&agrave; thời gian ghi tr&ecirc;n bao b&igrave; của c&aacute;c loại ph&acirc;n.</p>\n<p>Ch&uacute; &yacute;: b&oacute;n ph&acirc;n c&aacute;ch gốc chừng 30cm.</p>\n<p>Thường xuy&ecirc;n l&agrave;m cỏ v&agrave; diệt s&acirc;u ăn l&aacute; cũng như những loại nấm th&acirc;n, l&aacute; cho c&acirc;y.</p>\n<p>Cắt tỉa:</p>\n<p>Sau khoảng 7 th&aacute;ng, l&uacute;c n&agrave;y c&acirc;y đ&atilde; ph&aacute;t t&aacute;n rộng, to v&agrave; cao hơn, ch&uacute;ng ta tiến h&agrave;nh tỉa c&agrave;nh cho c&acirc;y, gi&uacute;p c&acirc;y ph&aacute;t triển tốt hơn, đ&acirc;m nhiều chồi hơn, tăng độ tho&aacute;ng cho c&acirc;y. Ta tỉa bằng c&aacute;ch cắt những c&agrave;nh gi&agrave;, s&acirc;u bệnh.</p>\n<p>Ch&uacute; &yacute;, trong 2 năm đầu nếu trồng bằng hạt th&igrave; n&ecirc;n ngắt hoa, cắt tỉa c&agrave;nh thường xuy&ecirc;n để tạo t&aacute;n cho c&acirc;y ph&aacute;t triển tốt hơn.</p>",
        enName: "Rice Leaf Blast",
        idDisease: key,
        idCategoriesPlant: 15,
        viName: "Bệnh đạo ôn lúa");
    myRef.set(post.toMap());

    // DiseasePlants post6 = DiseasePlants(
    //     describe:
    //         "<p><strong>T&ecirc;n tiếng anh: Powdery mildew (Cherry)</strong></p>\n<p><strong>T&ecirc;n tiếng việt: Bệnh phấn trắng (Anh đ&agrave;o)<br /></strong></p>\n<p><strong>Giới thiệu</strong></p>\n<p>Bệnh nấm phấn trắng l&agrave; t&ecirc;n gọi của một nh&oacute;m bệnh do một số lo&agrave;i nấm c&oacute; họ h&agrave;ng gần g&acirc;y ra. Dễ d&agrave;ng nhận biết bệnh nấm phấn trắng tr&ecirc;n c&acirc;y trồng qua lớp phấn trắng mọc ở cả hai mặt của l&aacute; v&agrave; đ&ocirc;i khi tr&ecirc;n hoa, quả v&agrave; th&acirc;n. Dấu hiệu đầu ti&ecirc;n của bệnh thường l&agrave; những đốm tr&ograve;n m&agrave;u trắng tr&ecirc;n l&aacute;. Về sau, c&aacute;c l&aacute; thường cuộn lại v&agrave; c&oacute; h&igrave;nh dạng m&oacute;p m&eacute;o; chuyển sang m&agrave;u v&agrave;ng hoặc m&agrave;u n&acirc;u, c&oacute; thể rụng sớm. Điều n&agrave;y c&oacute; thể khiến c&acirc;y suy yếu dần; v&agrave; nếu k&eacute;o d&agrave;i sẽ dẫn đến t&igrave;nh trạng mất khả năng phục hồi v&agrave; chết c&acirc;y trồng.</p>\n<p><strong>Triệu chứng</strong></p>\n<ul>\n<li>Triệu chứng đầu ti&ecirc;n l&agrave; vết bệnh h&igrave;nh tr&ograve;n, m&agrave;u xanh nhạt tr&ecirc;n cả hai bề mặt l&aacute;. Khu vực bị nhiễm bệnh sẽ ph&aacute;t triển một đ&aacute;m nhỏ giống như b&ocirc;ng m&agrave;u trắng.&nbsp;Sợi nấm ph&aacute;t triển c&oacute; thể thưa thớt v&agrave; kh&oacute; ph&aacute;t hiện.&nbsp;C&aacute;c triệu chứng ban đầu c&oacute; thể dễ d&agrave;ng ph&aacute;t hiện hơn khi mặt trời l&oacute; dạng sau vai l&agrave;m nổi bật khu vực n&agrave;y.&nbsp;</li>\n<li>Nhiễm tr&ugrave;ng l&aacute; nặng c&oacute; thể dẫn đến quăn v&agrave; / hoặc phồng rộp, v&agrave; c&aacute;c l&aacute; được bao phủ bởi sự ph&aacute;t triển giống như b&ocirc;ng trắng đặc trưng.&nbsp;Khi l&aacute; bị nhiễm bệnh gi&agrave; đi, c&aacute;c th&acirc;n nhỏ, m&agrave;u đen (chasmothecia) được h&igrave;nh th&agrave;nh</li>\n</ul>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://pnwhandbooks.org/sites/pnwhandbooks/files/plant/images/cherry-prunus-spp-powdery-mildew/dsc0045.jpg\" width=\"333\" height=\"221\" /></p>\n<p><strong>V&ograve;ng đời</strong></p>\n<ul>\n<li>Những đốm phấn trắng nhỏ tr&ecirc;n l&aacute; thường l&agrave; dấu hiệu đầu ti&ecirc;n của bệnh phấn trắng. Những đốm &nbsp;trắng n&agrave;y l&agrave; giai đoạn của sợi nấm v&agrave; l&agrave; giai đoạn đầu g&acirc;y hại.</li>\n<li>Khi thời tiết chuyển sang lạnh; sợi nấm c&oacute; thể ngủ đ&ocirc;ng v&agrave; qua m&ugrave;a đ&ocirc;ng trong chồi hoặc c&aacute;c bộ phận được che chở kh&aacute;c của c&acirc;y; hoặc c&oacute; thể h&igrave;nh th&agrave;nh c&aacute;c cấu tr&uacute;c b&agrave;o tử mở ra v&agrave;o cuối m&ugrave;a h&egrave; hoặc rơi trước những cơn mưa m&ugrave;a đ&ocirc;ng.</li>\n<li>B&agrave;o tử nấm c&oacute; thể được ph&oacute;ng th&iacute;ch bất cứ l&uacute;c n&agrave;o trong m&ugrave;a sinh trưởng nếu điều kiện thuận lợi. Điều n&agrave;y thường nằm trong khoảng 15 độ &nbsp;đến 26 độ. Nhiệt độ tr&ecirc;n 32 độ c&oacute; thể giết chết nấm g&acirc;y bệnh.</li>\n<li>Về mặt kỹ thuật, c&oacute; nhiều lo&agrave;i bệnh phấn trắng, mỗi lo&agrave;i chỉ tấn c&ocirc;ng một số c&acirc;y nhất định. Hầu hết c&aacute;c lo&agrave;i đều c&oacute; v&ograve;ng đời tương tự nhau.</li>\n</ul>\n<p><strong>C&aacute;ch chữa trị v&agrave; ph&ograve;ng bệnh phấn trắng</strong></p>\n<ul>\n<li><strong>Baking Soda</strong>. &nbsp;Bản th&acirc;n baking soda thường kh&ocirc;ng hiệu quả như một phương ph&aacute;p điều trị bệnh phấn trắng; nhưng khi n&oacute; được kết hợp với x&agrave; ph&ograve;ng lỏng v&agrave; nước; n&oacute; c&oacute; thể l&agrave; một vũ kh&iacute; lợi hại. D&ugrave;ng Banking soda được sử dụng như một biện ph&aacute;p ph&ograve;ng ngừa hơn l&agrave; một phương ph&aacute;p điều trị. Kết hợp một muỗng canh baking soda v&agrave; một nửa muỗng c&agrave; ph&ecirc; x&agrave; ph&ograve;ng lỏng; kh&ocirc;ng chứa chất tẩy rửa với một gallon nước v&agrave; phun hỗn hợp n&agrave;y l&ecirc;n c&acirc;y.</li>\n<li><strong>Nước s&uacute;c miệng.&nbsp;</strong> Nước s&uacute;c miệng bạn c&oacute; thể sử dụng h&agrave;ng ng&agrave;y để ti&ecirc;u diệt vi tr&ugrave;ng trong miệng; cũng c&oacute; thể c&oacute; hiệu quả trong việc ti&ecirc;u diệt c&aacute;c b&agrave;o tử bệnh phấn trắng. V&igrave; chức năng của n&oacute; l&agrave; ti&ecirc;u diệt vi tr&ugrave;ng; n&ecirc;n c&aacute;c b&agrave;o tử bệnh phấn trắng kh&ocirc;ng thể chống chọi được. Sử dụng ba phần nước với một phần nước s&uacute;c miệng l&agrave; một tỷ lệ tốt; nhưng phần mọc mới c&oacute; thể bị hỏng v&igrave; nước s&uacute;c miệng rất mạnh; v&igrave; vậy h&atilde;y thận trọng khi sử dụng.</li>\n<li><strong>Sữa.</strong> &nbsp;Sữa đang được đưa v&agrave;o hiện trường như một phương tiện hữu hiệu để kiểm so&aacute;t bệnh phấn trắng. Kh&ocirc;ng phải khoa học n&agrave;o cũng biết; nhưng c&aacute;c hợp chất trong sữa c&oacute; thể hoạt động như một chất khử tr&ugrave;ng v&agrave; diệt nấm cũng như c&oacute; khả năng tăng khả năng miễn dịch tổng thể của c&acirc;y. N&oacute; c&oacute; xu hướng hiệu quả như một phương ph&aacute;p ngăn ngừa bệnh phấn trắng tr&ecirc;n b&iacute; xanh v&agrave; c&aacute;c loại b&iacute; kh&aacute;c; cũng như dưa chuột. Tỷ lệ hỗn hợp hiệu quả l&agrave; khoảng một phần sữa với hai hoặc ba phần nước.</li>\n<li><strong>Nước.</strong> &nbsp;V&igrave; điều kiện kh&ocirc; c&ugrave;ng với độ ẩm cao; thường l&agrave; thủ phạm đằng sau sự ph&aacute;t triển của bệnh phấn trắng; n&ecirc;n bạn c&oacute; thể tưới nước cho c&acirc;y từ tr&ecirc;n cao v&agrave; l&agrave;m ướt to&agrave;n bộ c&acirc;y. Tuy nhi&ecirc;n; điều quan trọng l&agrave; sử dụng phương ph&aacute;p n&agrave;y một c&aacute;ch tiết kiệm; v&igrave; tưới qu&aacute; nhiều c&oacute; thể g&acirc;y ra c&aacute;c vấn đề kh&aacute;c cho c&acirc;y của bạn.</li>\n<li><strong>Sanodyna. </strong>Xịt trực tiếp dung dịch l&ecirc;n v&ugrave;ng bị bệnh; thực hiện thường xuy&ecirc;n, 2-3 lần/ tuần v&ugrave;ng bị phấn trắng sẽ được kiểm so&aacute;t v&agrave; ph&ograve;ng ngừa l&acirc;y lan.</li>\n</ul>",
    //     enName: "Powdery mildew (Cherry)",
    //     idDisease: 6,
    //     idCategoriesPlant: 3,
    //     viName: "Bệnh phấn trắng (Anh đào)");
    // myRef.set(post6.toMap());

    // DiseasePlants post7 = DiseasePlants(
    //     describe:
    //         "<p><strong>T&ecirc;n tiếng Anh: Cherry</strong></p>\n<p><strong>T&ecirc;n tiếng Việt: Anh đ&agrave;o</strong></p>\n<p><strong>Đặc điểm:&nbsp;</strong></p>\n<p>- L&agrave; một loại c&acirc;y thuộc c&acirc;y th&acirc;n gỗ, nhiều c&agrave;nh nh&aacute;nh, sống l&acirc;u năm v&agrave; c&oacute; chiều cao l&ecirc;n đến 10m. Cherry được trồng chủ yếu ở v&ugrave;ng &ocirc;n đới. Th&ocirc;ng thường, m&ugrave;a thu hoạch quả cherry l&agrave; từ th&aacute;ng 5 đến th&aacute;ng t&aacute;m.</p>\n<p dir=\"ltr\">- Th&acirc;n c&acirc;y cherry m&agrave;u x&aacute;m bạc, gỗ rất cứng. L&aacute; cherry c&oacute; m&agrave;u xanh đậm, h&igrave;nh bầu dục thu&ocirc;n d&agrave;i, nhọn v&agrave; mỏng về ph&iacute;a ngọn, m&eacute;p l&aacute; c&oacute; răng cưa, g&acirc;n nổi r&otilde;. Tr&ocirc;ng l&aacute; cũng kh&aacute; giống l&aacute; c&acirc;y trứng c&aacute;. Hoa cherry m&agrave;u trắng hồng, đ&ocirc;i khi l&agrave; hồng đậm, c&oacute; năm c&aacute;nh tr&ocirc;ng rất giống hoa tường vi, thường nở quanh năm.</p>\n<p dir=\"ltr\">- Quả cherry mọc th&agrave;nh ch&ugrave;m tr&ecirc;n cuống d&agrave;i đung đưa theo gi&oacute;. Ban đầu quả cherry c&oacute; m&agrave;u xanh, khi ch&iacute;n c&oacute; m&agrave;u đỏ mọng, m&agrave;u v&agrave;ng hoặc đen, đường k&iacute;nh quả khoảng 2,5cm.</p>\n<p>- Quả cherry c&oacute; vị ngọt, da b&oacute;ng, thịt mọng &oacute;ng ả như ngọc b&iacute;ch, ăn gi&ograve;n ngọt, đậm đ&agrave;, hương thơm dịu nhẹ v&agrave; hạt r&oacute;c kh&ocirc;ng d&iacute;nh.</p>\n<p>- Đ&acirc;y được xem l&agrave; loại quả si&ecirc;u dinh dưỡng, h&agrave;m lượng Vitamin A &amp; C rất cao kết hợp một số loại Vitamin đặc biệt chứa chất oxy h&oacute;a c&oacute; thể ngăn ngừa được c&aacute;c g&ocirc;c tự do g&acirc;y tổn thương đến&nbsp; tế b&agrave;o. Ngo&agrave;i ra Cherry&nbsp;c&ograve;n c&oacute; lợi &iacute;ch gi&uacute;p cơ thể khỏe mạnh v&agrave; cải thiện một số bệnh như: B&eacute;o ph&igrave;, bệnh khớp, bệnh cao huyết &aacute;p, tim mạch&hellip;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://vn-live-01.slatic.net/p/16175197a82c826f4a159eb3b11181c3.jpg\" alt=\"COMBO 05 Hạt Giống C&Acirc;Y CHERRY MỸ H&agrave;ng Nhập Khẩu, nhiều m&agrave;u | Lazada.vn\" width=\"294\" height=\"201\" /></p>\n<p><strong>Kỹ thuật chăm s&oacute;c:</strong></p>\n<p>- Tưới nước:</p>\n<ul>\n<li>Đối với nho trồng tr&ecirc;n đất c&aacute;t th&igrave; 5 &ndash; 7 ng&agrave;y tưới một lần, đối với thời điểm ra l&aacute;, hoa v&agrave; nu&ocirc;i quả th&igrave; tần suất l&agrave; 3 &ndash; 5 ng&agrave;y một lần tưới nước.</li>\n<li>Đối với nho trồng đất thịt th&igrave; tần suất tưới cần &iacute;t hơn, trong đ&oacute; th&ocirc;ng thường l&agrave; 10 &ndash; 15 ng&agrave;y tưới một lần, trong giai đoạn ra quả cần n&acirc;ng l&ecirc;n l&agrave; 7 &ndash; 10 ng&agrave;y tưới một lần l&agrave; th&iacute;ch hợp.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</li>\n</ul>\n<p>- Xới x&aacute;o: Xới x&aacute;o thực hiện gi&uacute;p l&agrave;m cỏ dưới gi&agrave;n nho, kh&ocirc;ng để mặt đất phơi ra nắng, đảm bảo kh&ocirc;ng để t&igrave;nh trạng đ&oacute;ng v&agrave;ng mỗi lần tưới nước được đảm bảo đ&aacute;p ứng tốt. Thực hiện xới x&aacute;o cho đất mỗi vụ mỗi lần để việc ph&aacute; bỏ đi rễ cũ, đồng thời t&aacute;i tạo rễ mới được tiến h&agrave;nh. Trong qu&aacute; tr&igrave;nh xới x&aacute;o th&igrave; việc b&oacute;n ph&acirc;n, trộn ph&acirc;n v&agrave;o đất cần thực hiện để n&acirc;ng cao hơn nữa dinh dưỡng c&oacute; trong đất, cung cấp đầy đủ cho c&acirc;y khi trồng.</p>\n<p>- Cho leo v&agrave; cắt tỉa:&nbsp; sử dụng s&agrave;o, cọc gỗ với độ lớn bằng ng&oacute;n tay c&aacute;i cắm gần vị tr&iacute; gốc nho, theo chiều thẳng đứng để tạo điều kiện cho th&acirc;n nho leo l&ecirc;n. L&uacute;c n&agrave;y, chọn ngọn khỏe nhất v&agrave; buộc trực tiếp v&agrave;o cọc để việc leo l&ecirc;n gi&agrave;n của c&acirc;y thuận lợi, hiệu quả. Ngo&agrave;i ra, đối với những ngọn phụ, nh&aacute;nh nhỏ th&igrave; sau đ&oacute; được cắt tỉa to&agrave;n bộ, s&aacute;t tới tận n&aacute;ch l&aacute; để dinh dưỡng tập trung nu&ocirc;i th&acirc;n ch&iacute;nh.</p>\n<p>Tới thời điểm ngọn ch&iacute;nh của c&acirc;y nho leo tới gi&agrave;n th&igrave; l&uacute;c ng&agrave;y thực hiện ngắt b&uacute;t sinh trường để c&agrave;nh cấp 1 c&oacute; điều kiện ph&aacute;t triển. Mỗi gốc nho th&ocirc;ng thường chỉ để lại từ 2 -3&nbsp; tay, hoặc c&oacute; thể l&agrave; 4 tay t&ugrave;y thuộc trực tiếp v&agrave;o từng giống nho để c&oacute; thể đưa ra quyết định th&iacute;ch hợp nhất.</p>\n<p>- B&oacute;n ph&acirc;n:&nbsp;</p>\n<ul>\n<li>Trước khi tiến h&agrave;nh trồng nho việc b&oacute;n l&oacute;t cần được thực hiện, liều lượng cụ thể l&agrave; sử dụng ph&acirc;n hữu cơ Oganic 1 hoặc Nutrifert 4-3-4 cho xuống hố trước khi trồng.</li>\n<li>B&oacute;n dung dịch với 40 gram ph&acirc;n v&agrave; 10 l&iacute;t nước tưới đều l&ecirc;n khu vực gốc nho sau khoảng v&agrave;i th&aacute;ng c&acirc;y được trồng xuống mặt đất.</li>\n<li>Sau v&agrave;i th&aacute;ng việc tiến h&agrave;nh rạch r&atilde;nh, tưới v&agrave; b&oacute;n ph&acirc;n cho nho được thực hiện với khoảng 50kg/ ha. Cần ch&uacute; &yacute; rằng mỗi khi rải ph&acirc;n xong cần tiến h&agrave;nh lấp đất cho phẳng l&ecirc;n khui vực r&atilde;nh.</li>\n<li>Thời điểm trước khi cắt c&agrave;nh cho c&acirc;y nho tiến h&agrave;nh d&ugrave;ng ph&acirc;n b&oacute;n NPK&nbsp;với liều lượng 100kg/ ha, ngo&agrave;i ra tiến h&agrave;nh phun ph&acirc;n b&oacute;n&nbsp;cho l&aacute;, thường sử dụng ph&acirc;n đầu tr&acirc;u.</li>\n<li>Trong thời gian c&acirc;y ra tr&aacute;i việc b&oacute;n ph&acirc;n NPK cần tiếp tục thực hiện với liều lượng l&agrave; 100kg/ ha, tiếp tục phun th&ecirc;m ph&acirc;n b&oacute;n l&aacute;.</li>\n<li>Khi tr&aacute;i nho lớn với k&iacute;ch thước bằng đầu ng&oacute;n tay &uacute;t th&igrave; l&uacute;c n&agrave;y b&oacute;n ph&acirc;n NPK với lượng cụ thể l&agrave; 150kg/ ha, phun th&ecirc;m ph&acirc;n b&oacute;n l&aacute; better KNO3.</li>\n<li>Việc thu hoạch sau khi ho&agrave;n th&agrave;nh th&igrave; l&uacute;c n&agrave;y xới đất, b&oacute;n th&ecirc;m ph&acirc;n hữu cơ HG01 v&agrave; ph&acirc;n NPK thực hiện đầy đủ để đảm bảo đủ dinh dưỡng trong đất cho vụ sau cung cấp cho c&acirc;y nho.</li>\n</ul>\n<p>&nbsp;</p>\n<p>&nbsp;</p>",
