class History {
  late String accuracy;
  late String date;
  late String time;
  late String describe;
  late String enName;
  late String viName;
  late String idDisease;
  late String image;
  late String historyKey;

  History(
      {required this.accuracy,
      required this.date,
      required this.time,
      required this.describe,
      required this.enName,
      required this.viName,
      required this.idDisease,
      required this.image,
      required this.historyKey});

  //sending data to firebase
  Map<String, dynamic> toMap() {
    return {
      'accuracy': accuracy,
      'date': date,
      'time': time,
      'describe': describe,
      'enName': enName,
      'viName': viName,
      'idDisease': idDisease,
      'image': image,
      'historyKey': historyKey
    };
  }

  History.fromValues(var json) {
    accuracy = json['accuracy'];
    date = json['date'];
    time = json['time'];
    describe = json['describe'];
    enName = json['enName'];
    viName = json['viName'];
    idDisease = json['idDisease'];
    image = json['image'];
    historyKey = json['historyKey'];
  }
}

